timePeriods.description:
  The night-time forest is obscured by gloomy darkness. Nevertheless, you can
hear the sounds of life around you. The path through the forest continues east;
but impenetrable darkness hides the way.

rooms.loxScript:
// This function gets called the first time a character logs in as an elf, 
// after character creation.
on_login(vch) {
    // Only newly-created characters
    if (vch.level > 1 or vch.race != Race.Elf)
        return;
        
    vch.send(
        "^*You step off the end of an old rope ladder at the base of "
        "Cuivealda, the Tree of Awakening. Your years in the nurturing "
        "hands of the Tetyayath have come to an end.^/^/"
        "Now you must go out into the world and find your destiny...^/^x")

    if (vch.can_quest(12000)) {
        delay(1, () -> {
            vch.send("^jYou have instructions to meet your old mentor in a "
                "clearing just to the east. There you will continue your "
                "training. Type '^*EXITS^j' to see what lies in that "
                "direction. Type '^*EAST^j' to go there.^x")
            vch.grant_quest(12000)
            vch.send("^jType '^*QUEST^j' to view your quest log.^x")
        })
    }
}

on_greet(vch) {
    if (vch.has_quest(12000)) {
        vch.send("^jTo read the note tacked to the tree, type any of the "
                 "following: ^*READ NOTE^j, ^*LOOK NOTE^j, or ^*EXAMINE "
                 "NOTE^j.^x")
    }
}

on_greet(vch) {
    if (vch.has_quest(12020)) {
        vch.send("^jLethariel's post is just ahead. Step east and report in.^x");
    }
}

on_greet(vch) {
    if (vch.has_quest(12050)) {
        vch.send("^jEnvoy Calendor needs Findorian's writ. Try ^*GIVE WRIT ENVOY^j when you are ready.^x");
    }
}

mobiles.loxScript:
on_greet(vch) {
    if (vch.can_finish_quest(12000)) {
        say("There you are, ${vch.name}; I'm glad to see you. I wish I could ease you into the brutal World Below, but we have no such luck.");
        echo("$n sighs.");
        say("I need your help with something, and it's not going to be pleasant.");
        vch.finish_quest(12000);
        say(
            "There is a blight in this region of the forest infecting flora and fauna alike. I want to get to the root of it, but for now there is great suffering that must be dealt with.",
            "This is the burden of a Forestspeaker; there is only one way to stop the spread of the disease and bring peace to the afflicted, and that is by death.",
            "The Tetyayath have taught you the Creeds, and you have spoken them. Now you must pay the terrible cost of following them.",
            "Go north. Bring those poor animals mercy. They have suffered long enough.");
        vch.grant_quest(12006);
        return;
    }
    else if (vch.can_finish_quest(12006)) {
        say("Bloody business, that. I'm sorry.");
        echo(vch, "$n places his hand on $S shoulder.");
        say(
            "What you have done here important. Preservation is about protecting what we can, and mitigating when we can't.",
            "You have honored the Creeds. You have honored the Forest.");
        vch.finish_quest(12006);
        say("I cannot leave this clearing, but Lethariel watches from the Verdant Watch east of here. Go to her.");
        if (vch.can_quest(12020))
            vch.grant_quest(12020);
        return;
    }
    else if (vch.can_finish_quest(12040)) {
        say("You silenced Vorenthas? Good. The reliquary will heal, but his madness proved what I feared.");
        say("He believed isolation would save us. Instead it blinded him to the danger creeping through our own roots.");
        vch.finish_quest(12040);
        say("Take this writ east to Envoy Calendor at the edge of Faladrin. He will carry our plea to Valewyn.");
        if (vch.can_quest(12050))
            vch.grant_quest(12050);
        return;
    }
    else if (vch.has_quest(12050)) {
        say("Envoy Calendor waits where the forest opens to the human watchfires. Give him my writ when you are ready to leave.");
        return;
    }
    else if (vch.has_quest(12040)) {
        say("Follow the hollow reliquary east. Vorenthas is bound to it by pride and poison; end both.");
        return;
    }
    else if (vch.has_quest(12020)) {
        say("Lethariel will know how the forest whispers now. She is due east beyond the watch.");
        return;
    }
    else if (vch.has_quest(12006)) {
        say("Grant mercy to the creatures north of here. Their suffering feeds something worse.");
        return;
    }
}

on_greet(vch) {
    if (vch.can_finish_quest(12020)) {
        say("Findorian trusts you, and so do I. Walk with care.");
        say("Bring me a sample from the weeping husk east of here; I need proof that this rot thinks for itself.");
        vch.finish_quest(12020);
        vch.grant_quest(12030);
        return;
    }
    if (vch.can_finish_quest(12030)) {
        say("This resin hums like a voice.");
        echo(vch, "$n uncorks a vial, capturing a thread of resin from ${vch.name}.");
        say("The pattern points to Vorenthas. He still squats in the hollow reliquary ahead, feeding the blight.");
        vch.finish_quest(12030);
        say("Cut him free so the forest can heal.");
        vch.grant_quest(12040);
        return;
    }
    if (vch.has_quest(12030)) {
        say("The sample we need clings to the weeping husk in the copse east of the resin chasm.");
        return;
    }
    if (vch.has_quest(12040)) {
        say("Vorenthas carved himself a throne in the reliquary. Do not let his doubts infect you.");
        return;
    }
    if (vch.has_quest(12020)) {
        say("Keep moving, ${vch.name}. The watch survives because we act before the rot does.");
    }
}

on_fight(victim) {
    if (!is_mob(victim))
        return;

    if (!this.hasAddressed) {
        say("Isolation failed us. I will carve a new covenant in sap and bone!");
        this.hasAddressed = true;
    }

    if (dice(1, 4) >= 3) {
        echo(victim, "^rBlack resin lashes out from the reliquary walls!^x");
        damage(this, victim, 6 + dice(1, 3), 0, DamageType.Acid, true);
    }
}

on_greet(vch) {
    if (vch.has_quest(12050)) {
        say("Hand me Findorian's writ and I will open the way to Valewyn.");
        return;
    }

    say("I hold the road for those who would carry Tauremar's plea beyond the boughs.");
}

objects.loxScript:
on_given(giver, taker) {
    if (!taker.is_mob())
        return;

    if (taker.vnum == 12026) {
        taker.say(giver, "I will see your words carried to Valewyn.");
        taker.echo(giver, "$n folds the writ with care and seals it under a palm of light.");
        if (giver.has_quest(12050)) {
            giver.finish_quest(12050);
            giver.send("^jCalendor gestures east toward the unfinished bridge.^x");
        }
        return;
    }

    giver.send("^rThe writ crackles and slips from ${taker.name}'s grasp, unwilling to trust another.^x");
}

on_dropped(dropper) {
    if (!is_mob(dropper))
        return;
    dropper.send("^rThe writ flutters, then drifts back toward your pack as if tugged by unseen roots.^x");
}
